let payload = msg.payload;
if (payload.length === 0) {
    payload = "unknown";
}

payload = payload.replace("armed_", "");
msg.payload = payload[0].toUpperCase() + payload.slice(1);

return msg;

function getSpeed(temp) {
    if(typeof temp !== "number" || temp < 72) {
        return 0;
    }

    if (temp < 76) {
        return 1;
    }

    if (temp < 80) {
        return 2;
    }

    return 3;
}

let oldState, newState;
if (msg.data === undefined) {
    newState = getSpeed(Number(msg.payload));
} else {
    oldState = getSpeed(msg.data.old_state.state);
    newState = getSpeed(msg.data.new_state.state);
}

return {
    payload: {
        changed: (oldState !== newState),
        value: newState
    }
}
